mapper.select:
select

from uni_customer


        and customer_num like concat('%',#{criteria.customerNum},'%')


        and customer_name like concat('%',#{criteria.customerName},'%')


        and invitation_code = #{criteria.invitationCode}


        AND create_time BETWEEN #{criteria.createTime[0]} AND #{criteria.createTime[1]}


        AND update_time BETWEEN #{criteria.updateTime[0]} AND #{criteria.updateTime[1]}


order by customer_id desc

SELECT us.customer_id AS customerId, us.customer_num AS customerNum, us.customer_name AS customerName, us.invitation_code AS invitationCode, us.password AS password, us.create_by AS createBy, us.update_by AS updateBy, us.create_time AS createTime, us.update_time AS updateTime, su.username AS username FROM uni_customer AS us LEFT JOIN user_invitation_code_bind AS ub ON us.invitation_code = ub.invitation_code LEFT JOIN eladmin.sys_user AS su on ub.user_id = su.user_id
and us.customer_num like concat('%',#{criteria.customerNum},'%')
and us.customer_name like concat('%',#{criteria.customerName},'%')
and us.invitation_code = #{criteria.invitationCode}
AND us.create_time BETWEEN #{criteria.createTime[0]} AND #{criteria.createTime[1]}
AND us.update_time BETWEEN #{criteria.updateTime[0]} AND #{criteria.updateTime[1]}
order by us.create_time desc
